// Load all the channels within this directory and all subdirectories.
// Channel files must be named *_channel.js.

const channels = require.context('.', true, /_channel\.js$/)
channels.keys().forEach(channels)

var $banner;

var t0 = performance.now()

window.addEventListener("load", function slide(){
    $banner = document.getElementById("Banner1")
})

$banner.hover(function teste(){
    $banner.style.marginTop = "100px"
})

function changeBanner(){
    var src = $banner.style.background

    var b0 = 'url("/assets/Banner0.jpg")'
    var b1 = 'url("/assets/Banner1.jpg")'

    if(src != b0 && src != b1){
        $banner.style.background = b1
    }else if(src == b1){
        $banner.style.background = b0
    }else{
        $banner.style.background = b1
    }

}

var time = setInterval(changeBanner, 2000)
