// Load all the channels within this directory and all subdirectories.
// Channel files must be named *_channel.js.

const channels = require.context('.', true, /_channel\.js$/)
channels.keys().forEach(channels)

var banner;

var t0 = performance.now()

window.addEventListener("load", function slide(){
    banner = document.getElementById("Banner1")
})

function change(){
    if(banner.style.backgroundColor == "black"){
        banner.style.backgroundColor = "blue"
    }else{
        banner.style.backgroundImage = "url(asset_path(Banner0.jpg))"
    }
}

var time = setInterval(change, 3000)
